import React, {useState, useEffect} from 'react';
import {Container} from "../../components/Styles/Container/Container.style";
import {useLocation, useNavigate} from "react-router-dom";
import Pagination from 'react-bootstrap/Pagination';
import ListGroup from 'react-bootstrap/ListGroup';
import Image from 'react-bootstrap/Image';
import axios from '../Request/RequestConfig.js';



const SoftwareData = async () => {
    const [loginError, setLoginError] = useState('');
    const [boardPG, setBoardPG] = useState({content: []});
    const navigate = useNavigate();

    const boardData = (productList => {
        return productList.map((currentProduct) => {
            const newData = {
                id: currentProduct.id,
                sku: "FAS-01",
                link: currentProduct.link,
                title: currentProduct.title,
                img: currentProduct.content.thumbnail,
                price: currentProduct.content.price,
                originPrice: currentProduct.content.originPrice,
                discountPrice: currentProduct.content.discountPrice,
                discountPercentage: currentProduct.content.discountPercentage,
                isNew: currentProduct.content.isNew,
                isHot: currentProduct.content.isHot,
                star: currentProduct.content.star,
                isFreeShipping: currentProduct.content.isFreeShipping,
                description: currentProduct.content.content
            };
            setBoardPG(prevData => [...prevData, newData]);
        })
    });

    try {
        const accessToken = localStorage.getItem('accessToken');
        const refreshToken = localStorage.getItem('refreshToken');

        if (accessToken && refreshToken) {
            const response = await axios.get("http://localhost:3005/auth/software", {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${accessToken}`,
                    'RefreshToken': `Bearer ${refreshToken}`,
                },
            });

            if (response.status == 200) {
                const data = await response.data;
                console.log(data);
                boardData(data);
            } else {
                setLoginError('인증된 유저만 접근 가능합니다.');
            }
        } else {
            setLoginError('로그인이 필요합니다.');
        }
    } catch (error) {
        console.error('인증되지 않은 사용자가 접근하려 합니다..', error);
        setLoginError('인증된 유저만 접근 가능합니다.');
    }
    return boardPG;
}

export default SoftwareData;
