import React, { lazy, Component } from "react";
import { ReactComponent as IconEnvelope } from "bootstrap-icons/icons/envelope.svg";
import { ReactComponent as IconHouse } from "bootstrap-icons/icons/house.svg";
import { ReactComponent as IconTelephone } from "bootstrap-icons/icons/telephone.svg";

const ContactUsForm = lazy(() => import("../../components/ContactUsForm"));

class ContactUsView extends Component {
  onSubmit = async (values) => {
    alert(JSON.stringify(values));
  };

  render() {
    return (
      <div className="container my-3">
        <div className="row g-3">
          <div className="col-md-8">
            <div className="card">
              <div className="card-header">
                <IconEnvelope className="i-va" /> Send Message
              </div>
              <div className="card-body">
                <ContactUsForm onSubmit={this.onSubmit} />
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <div className="card mb-3">
              <div className="card-header">
                <IconHouse className="i-va" /> Address
              </div>
              <div className="card-body">
                <h6 className="card-title border-bottom border-dark pb-2">Head Office</h6>
                <address>
                  <strong>Twitter, Inc.</strong>
                  <br />
                  1355 Market St, Suite 900
                  <br />
                  San Francisco, CA 94103
                  <br />
                  <IconTelephone className="i-va" />{" "}
                  <abbr title="Phone">P:</abbr> (123) 456-7890
                </address>
                <h6 className="card-title border-bottom border-dark pb-2">Development Office</h6>
                <address>
                  <strong>Twitter, Inc.</strong>
                  <br />
                  1355 Market St, Suite 900
                  <br />
                  San Francisco, CA 94103
                  <br />
                  <IconTelephone className="i-va" />{" "}
                  <abbr title="Phone">P:</abbr> (123) 456-7890
                </address>
              </div>
            </div>
            <div className="card">
              <div className="google-maps">
                <iframe
                  src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d313635.5491853188!2d-122.57606416467848!3d37.20933611930123!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x8085815c67b3754d%3A0xb42714f3436732f2!2s1355%20Market%20St%20%23900%2C%20San%20Francisco%2C%20CA%2094103%2C%20USA!5e0!3m2!1sen!2sin!4v1599193189366!5m2!1sen!2sin"
                  width={400}
                  height={300}
                  frameBorder={0}
                  style={{ border: 0 }}
                  allowFullScreen
                  aria-hidden="false"
                  tabIndex={0}
                  title="Location"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ContactUsView;
